import './App.css';
import {Button, Input} from 'react-bootstrap/';
import { useState } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faSun, faMoon, faS } from '@fortawesome/free-solid-svg-icons'

function App() {
  const [mode, setmode] = useState(faSun)
  const [bodymode, setbodymode] = useState("#FFFF")
  const [bodyColor, setBodyColor] = useState("#")
  const [modeTitle, setModeTitle] = useState("Light")

  const changemode = () => {
    if(mode == faSun) {
      setmode(faMoon)
      setModeTitle("Dark")
      setBodyColor("#09072c")
      console.log("Dark")
    } else {
      setmode(faSun)
      setModeTitle("Light")
      setBodyColor("#FFFF")

      console.log("Light")
    }

  }
  return (
    <div className="app" style={{backgroundColor: {bodyColor}}}>
        <div className="mode" style={{color: {bodyColor}}}>
            <h4>Youteube to mp3</h4>
            <button onClick={changemode} className='modeBtn'>{modeTitle} <FontAwesomeIcon icon={mode} /> </button>
        </div>
        <form className="form">

        </form>
    </div>
  );
}

export default App;
